import { createGlobalStyle } from 'styled-components'

export const GlobalStyle = createGlobalStyle`
    :root {
        --background: #f0f2f5;
        --red: #e52e4d;
        --blue: #5429cc;

        --blue-light: #6933ff;

        --text-title: #363f5f;
        --text-body: #969cb3;

        --shape: #FFFFFF;
    }
    
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    html {
        //font-size padr達o vem de 16px
        @media (max-width: 1080px) {
            font-size: 93.75%; //15px
        }

        @media (max-width: 720px) {
            font-size: 87.5%; //14px
        }
    }

    body {
        background: var(--background);
        //Deixa as fontes mais nitidas me nav que usem Webkit
        -webkit-font-smoothing: antialiased;
    }
    
    body, input, textarea, button {
        
    }

    button {
        cursor: pointer;
    }
    //Pega tudo que tiver desabilidado
    [disabled] {
        opacity: 0.6;
        //Vai mostar o ponteiro como n達o permitido
        cursor: not-allowed;
    }
`
