import styled from "styled-components";

export const Container =  styled.div`
/*Serve para deixar os 3 itens um do lado do outro
Porque são do mesmo tamanho!*/
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 2rem;
    margin-top: -10rem;
`;
